mapper.select:
SELECT bsd.id AS sdId, bsd.supplier_name , bsd.sd_no , bsd.trade , bsd.type , bsd.legperson , bsd.reqperson , bsd.province , bsd.city , bsd.req_phone , bsd.forshort , bsd.address , bsd.logo , bsd.chain_if , bsd.bz , bsd.supplier_status , bsd.register_num , bsd.legperson_type , bsd.business_file , bsd.register_file , bsd.certificate , bsd.review_id AS sd_review_id, bsd.review_status AS sd_review_status, bsd.review_remarks AS sd_review_remarks, bsd.create_time AS sd_create_time, bspa.id AS spaId, bspa.sd_id , bspa.account_num , bspa.trade_num , bspa.account_book , CAST((bspa.recharge)/10000 AS DECIMAL(18,4)) AS recharge , bspa.recharge_way, bspa.add_file , bspa.add_bz , bspa.create_id AS spa_create_id , bspa.review_id AS spa_review_id, bspa.review_status AS spa_review_status, (SELECT CAST((SUM(bspa.`recharge`))/10000 AS DECIMAL(18,4)) AS rechargeall FROM supplier_pay_addcount bspa) AS rechargeall, bspa.review_time AS spa_review_time, bspa.create_time AS spa_create_time FROM supplier_data bsd,supplier_pay_addcount bspa
bsd.id=bspa.sd_id
and bspa.sd_id = #{queryWrapper.sdId}
and bspa.account_book = #{queryWrapper.accountBook}
and bspa.trade_num = #{queryWrapper.tradeNum}
and bspa.review_status = #{reviewStatus}
and bspa.create_time >= #{queryWrapper.spaCreateBeginTime}
and bspa.create_time < #{queryWrapper.spaCreateEndTime}
and bsd.sd_no = #{queryWrapper.sdNo}
and bsd.supplier_name like concat('%', #{queryWrapper.supplierName}, '%')
